public void traverseArray(int[] arr) {
    // for loop, use .length for arrays
    for(int i = 0; i < arr.length; i++) {
        System.out.println(arr[i]);
    }
    
    //while loop
    int count = 0;
    while(count < arr.length) {
        System.out.println(arr[count]);
        count++;
    }
    
    // enhanced for loop
    for(int num : arr) {
        System.out.println(num);
    }
}

public void traverseArrayList(ArrayList<Integer> list) {
    // for loop, use .size() for arraylists
    for(int i = 0; i < list.size(); i++) {
        System.out.println(list.get(i));
    }
}

public void traverseMatrix(int[][] matrix) {
    // Iterate through all rows
    // matrix.length means the length of the matrix or the number of rows
    for(int i = 0; i < matrix.length; i++) {
        // Iterate through all columns of every row
        // matrix[0].length means the length of each row or the number of columns in each row
        for(int j = 0; j < matrix[0].length; j++) {
            System.out.println(matrix[i][j]);
        }
    }
}
